export type ConsoleSystem =
    | 'NES' | 'SNES' | 'N64' | 'GB' | 'GBC' | 'GBA'
    | 'GENESIS' | 'MASTER_SYSTEM' | 'GAME_GEAR'
    | 'PS1' | 'NEOGEO' | 'NEOGEO_POCKET'
    | 'PC_ENGINE' | 'WONDERSWAN' | 'VIRTUAL_BOY' | 'LYNX'
    | 'ATARI' | 'ARCADE' | 'KOIN' | 'MIX' | 'FLOPPY' | 'DEFAULT';

interface ConsoleIconProps {
    system: string;
    className?: string;
    size?: number;
}

