                {/* Main cabinet body - tall rectangular shape */}
                <rect x="4" y="2" width="16" height="20" rx="0.5" />
                {/* Top marquee area */}
                <rect x="5" y="2.5" width="14" height="3" rx="0.3" fill="black" opacity="0.3" />
